function myParticle(){this.x=Math.random()*W,this.y=Math.random()*H}function draw(){ctx.fillStyle="black",ctx.fillRect(0,0,W,H);for(var t=0;t<particles.length;t++){var a=particles[t];ctx.beginPath(),ctx.fillStyle="white",ctx.arc(a.x,a.y,40,2*Math.PI,!1),ctx.fill(),a.x++,a.y++}}for(var canvas=document.getElementById("canvas"),ctx=canvas.getContext("2d"),W=500,H=500,particles=[],i=0;50>i;i++)particles.push(new myParticle);var x=100,y=100;setInterval(draw,33);