function myParticle(){this.x=Math.random()*W,this.y=Math.random()*H,this.vx=20*Math.random()-10,this.vy=20*Math.random()-10}function draw(){ctx.fillStyle="black",ctx.fillRect(0,0,W,H);for(var t=0;t<particles.length;t++){var a=particles[t];ctx.beginPath(),ctx.fillStyle="white",ctx.arc(a.x,a.y,Math.random(),2*Math.PI,!1),ctx.fill(),a.x+=a.vx,a.y+=a.vy,a.x<-50&&(a.x=W+50),a.y<-50&&(a.y=H+50),a.x>W+50&&(a.x=-50),a.y>H+50&&(a.y=-50)}}for(var canvas=document.getElementById("canvas"),ctx=canvas.getContext("2d"),W=1e3,H=1e3,particles=[],i=0;2e3>i;i++)particles.push(new myParticle);var x=100,y=100;setInterval(draw,33);