function onDeviceReady(){pictureSource=navigator.camera.PictureSourceType,destinationType=navigator.camera.DestinationType}function onPhotoDataSuccess(e){var t=document.getElementById("smallImage");t.style.display="block",t.src="data:image/jpeg;base64,"+e}function onPhotoURISuccess(e){var t=document.getElementById("largeImage");t.style.display="block",t.src=e}function capturePhoto(){navigator.camera.getPicture(onPhotoDataSuccess,onFail,{quality:50,destinationType:destinationType.DATA_URL})}function capturePhotoEdit(){navigator.camera.getPicture(onPhotoDataSuccess,onFail,{quality:20,allowEdit:!0,destinationType:destinationType.DATA_URL})}function getPhoto(e){navigator.camera.getPicture(onPhotoURISuccess,onFail,{quality:50,destinationType:destinationType.FILE_URI,sourceType:e})}function onFail(e){alert("Failed because: "+e)}var pictureSource,destinationType;document.addEventListener("deviceready",onDeviceReady,!1);